INSERT INTO `services` VALUES (15, 14, 'لوگو تصویری', 'لوگو تصویری توضیحات', 'لوگو-تصویری', 'fas fa-image', 'طراحی لوگو', 0, NULL, NULL, '<p>-طراحی لوگو تصویری یکی از عناصر و ارکان اصلی&nbsp;<a href=\"https://dayaartz.com/services/%D8%B7%D8%B1%D8%A7%D8%AD%DB%8C-%D9%84%D9%88%DA%AF%D9%88\" target=\"_blank\" rel=\"noopener\">طراحی لوگو</a>&nbsp;در جهان طراحی گرافیک است. لوگو توصویری همانطور که از اسم و عنوانش پیداست شامل تصویر و جلوه ایی از بصر و شکل است که تجسم شخصیت برند شما را به مخاطبان نشان می&zwnj;دهد. شخصیت برند و کسب و کار شما در مجموعه ایی از رنگ و شکل و نماد های عینی طراحی می&zwnj;شود. دایا آرتز به عنوان یک استارت آپ سفارش آنلاین خدمات طراحی گرافیک، همیشه تلاش کرده است که بهترین سرویس های طراحی گرافیک را متناسب با نیاز کارفرمایان خود ارائه دهند؛ از همین جهت در ادامه توضیحات کاملی در خصوص طراحی لوگو تصویری برای شما فراهم شده است تا اطلاعات تکمیل تری نسبت به استراتژی انتخاب لوگو تصویری برای آغاز کامل سازی برند خود داشته باشید.</p>\n<h2>لوگو تصویری چیست؟ و چه مزیت هایی دارد؟</h2>\n<p>-طراحی لوگو تصویری که با عنوان های لوگو نمادین یا لوگو مارک و نشان تصویری نیز شناخته می&zwnj;شود، یکی از شیوه های طراحی لوگو است که در آن یک تصویر به عنوان نمادی از کسب و کار شما به مخاطب معرفی شده و مشتریان و مخاطبان شما، آن نماد را نشانه ایی از کسب و کار شما خواهند دانست. لوگو تصویری چندین و چند گونه مختلف دارد. ممکن است برگرفته از یک جسم باشد یا حتی یک شخصیت یا یک حیوان را درون خود به نمایش بگذارد. طراحی لوگو تصویری آسان نیست اما یقینا تشکیل یک فلسفه فکری مناسب برای چنین لوگو ایی بسیار سخت تر از&nbsp;<a title=\"طراحی لوگو نوشتاری\" href=\"https://dayaartz.com/services/%D8%B7%D8%B1%D8%A7%D8%AD%DB%8C-%D9%84%D9%88%DA%AF%D9%88-%D9%86%D9%88%D8%B4%D8%AA%D8%A7%D8%B1%DB%8C-%D8%AA%D8%A7%DB%8C%D9%BE%D9%88%DA%AF%D8%B1%D8%A7%D9%81%DB%8C\" target=\"_blank\" rel=\"noopener\">طراحی لوگو نوشتاری</a>&nbsp;است. به عقیده دایا آرتز لوگو تصویری یکی از بهترین روش ها برای انتقال مفهوم و زیرمتن در برند سازی است؛ چراکه تصویر و حافظه تصویری بسیاری از مردم بهتر از حافظه نوشتاری یا حافظه نام سپردنی آن ها است و برند هایی که در لوگو خودشان از نام برندشان در قالب نوشتار استفاده کرده اند، پروسه برندسازی متفاوتی را گذرانده اند.</p>\n<h2>انواع شیوه های طراحی لوگو تصویری شامل چه مواردی می&zwnj;شود ؟</h2>\n<h3><sub>-این دسته بندی ها توسط تیم طراحی گرافیک دایا آرتز انجام شده است و ممکن است طراحان مختلف دیدگاه های متفاوتی نسبت به طراحی لوگو تصویری داشته باشند اما تمامی موارد زیر بله لحاظ داشتن یک تصویر غیر متنی و نوشتاری زیرمجموعه ایی از طراحی لوگو تصویری هستند :</sub></h3>\n<ol>\n<li>طراحی لوگو تصویری در قالب اجسام حقیقی یا تصاویر وقاعی و رئال&nbsp;</li>\n<li>طراحی لوگو تصویری در قالب یک مفهوم انتزاعی یا غیر حقیقی</li>\n<li>طراحی لوگو تصویری در قالب یک کاراکتر یا شخصیت که با عنوان \"مَسکات\" شناخته می&zwnj;شود</li>\n<li>طراحی لوگو تصویری در قالب یک سمبول و نماد غیر متنی&nbsp;</li>\n</ol>', 0, NULL);
